mapper.select:
		select ROWNUM ,b_no as no, b_title as title, b_wdate as wdate, b_auth as auth, b_hit as hit, boardCode as category,boardname
               from (SELECT *
               FROM (select ROWNUM as rnum, b.b_no, b.b_title, b.b_wdate, b.b_auth, b.b_hit, b.boardCode, c.boardname
                        from board b , boardcategory c
                        where b.boardcode = c.boardcode and b.boardcode=#{boardCode}
                        order by b_no desc
                    )  
              where rnum  <=  #{pageNum}*#{rowcnt} 
            )
       w where rnum >= (#{pageNum}  -1)*(#{rowcnt}  + 1)

select rownum, b_no as no, b_title as title, b_auth as auth, b_hit as hit , b_wdate as wdate ,page ,boardCode as category, boardname from(select * from ((SELECT TRUNC((ROWNUM-1)/12+1) AS page, b_no, b_title, b_auth, b_hit , b_wdate, boardcode, boardname FROM (SELECT b.b_no, b.b_title, b.b_auth, b.b_content , b.b_wdate,b_hit, b.boardcode, c.boardname FROM board b, boardcategory c
b_title like '%${content}%' and b.boardcode=c.boardcode order by b_no desc)))) where page=${no} and boardcode=#{category}
B_CONTENT like '%${content}%' and b.boardcode=c.boardcode order by b_no desc)))) where page=${no} and boardcode=#{category}

   SELECT count(*) count FROM board
   
   
      b_title like '%${content}%' and boardCode = #{category}
   
   
   
      b_content like '%${content}%' and boardCode = #{category}

     	select b_no, comm_no as no, comm_auth as auth , COMM_CONTENT as content 
 		  from BoardComment
		 where b_no=#{no}
  		   and boardcode=#{category}
